import React, { useEffect, useState } from 'react'
import { IItemData } from '../../interfaces/IItemData'
import ItemDataState from '../../store/ItemDataState'
import ResponseDataState from '../../store/ResponseDataState'
import ProductSelectsState from '../../store/ProductSelectsState'
import './MemorySelect.scss'
import { observer } from 'mobx-react-lite'

const MemorySelect = observer(({ product }: { product: IItemData }) => {

    const { responseData } = ResponseDataState
    const { selectedColor } = ProductSelectsState

    const [productWithSameTitle, setProductWithSameTitle] = useState<IItemData[]>([])
    const [memoryList, setMemoryList] = useState<string[]>([])
    const [currentProductMemory, setCurrentProductMemory] = useState<string>()

    useEffect(() => {
        if (responseData) {
            let memory: string[] = []

            responseData.items.forEach((item: IItemData) => {
                if (product.title === item.title && !memory.includes(item.features.memory)) {
                    memory.push(item.features.memory)
                }

            })
            setMemoryList(memory)
            // setProductWithSameTitle(responseData.items.filter((item) => {
            //     return item.title === product.title
            // }))
            console.log(memory)
        }

        setCurrentProductMemory(product.features.memory)
    }, [])

    useEffect(() => {
        currentProductMemory && ProductSelectsState.setSelectedMemory(currentProductMemory)
    }, [selectedColor])


    return (
        <div className="memory-select">
            <h3 className="memory-select__colors-title">Объем памяти</h3>
            <ul className="memory-select__memory-list">
                {memoryList.map((memory: string) => {

                    return (
                        <li className={currentProductMemory === memory ? "memory-select__memory-wrap memory-select__memory-wrap--active" : "memory-select__memory-wrap"}
                        // style={product.color === color ? borderColor : undefined}
                        >

                            <button
                                className={
                                    currentProductMemory === memory
                                        ? "memory-select__memory-btn memory-select__memory-btn--active"
                                        : "memory-select__memory-btn"
                                }
                                // style={btnColor}
                                type="button"
                            // onClick={() => changeColor(product)}
                            >
                                {memory}
                            </button>
                        </li>
                    );
                })}

            </ul>
        </div>
    )
})

export default MemorySelect
