import React, { useEffect, useState } from 'react'
import './Pagination.scss'


interface IPagination {
    itemsCount: number | undefined;
    itemsPerPage: number | undefined;
    startIndex: number | undefined;
    setStartIndex: any;
}

const Pagination: React.FC<IPagination> = ({ itemsCount, itemsPerPage, startIndex, setStartIndex }) => {


    const [numbersButtons, setNumbersButtons] = useState<number[] | undefined>();


    useEffect(() => {

        let arrOfNumbers: number[] = []

        if (itemsCount && itemsPerPage) {

            for (let i = 1; i <= Math.ceil(itemsCount / itemsPerPage); i++) {
                arrOfNumbers.push(i)
            }
        }

        setNumbersButtons(arrOfNumbers)
    }, [])


    const switchPage = (number: number) => {
        itemsPerPage && setStartIndex(number * itemsPerPage)
        // itemsPerPage && console.log(number * itemsPerPage)
    }

    itemsPerPage && startIndex && console.log(startIndex / itemsPerPage)

    return (
        <div className='pagination'>
            <ul className='pagination__list'>
                {numbersButtons?.map((number) => {



                    return (
                        <li className='pagination__item'>
                            <button className={(itemsPerPage && startIndex && startIndex / itemsPerPage + 1 === number) ? 'pagination__btn-number active' : 'pagination__btn-number'} onClick={() => switchPage(number - 1)}>{number}</button>
                        </li>
                    )
                })}
            </ul>
        </div>
    )
}

export default Pagination
