import { RecordWithTtl } from 'dns';
import React, { useState } from 'react';
import { IItemData } from '../../../interfaces/IItemData';
import CartState from '../../../store/CartState'
import './Counter.scss'

interface ICounter {
    item: IItemData
}

const Counter: React.FC<ICounter> = ({ item }) => {

    const { cartItems } = CartState

    const [itemCount, setItemCount] = useState(1)

    const incItem = () => {
        setItemCount(itemCount + 1)
        CartState.setCartItems([...cartItems.concat(item)])
    }


    const decItem = () => {
        setItemCount(itemCount - 1)

    }

    return (
        <div className="counter">
            <button className="counter__btn counter__btn--dec"
                onClick={decItem}
            >-</button>
            <div className="counter__num">{itemCount}</div>
            <button className="counter__btn counter__btn--inc"
                onClick={incItem}
            >+</button>
        </div>
    )
}

export default Counter
