import React, { useEffect, useState } from 'react'
import { IItemData } from '../../interfaces/IItemData'
import ItemDataState from '../../store/ItemDataState'
import ResponseDataState from '../../store/ResponseDataState'
import './MemorySelect.scss'

const MemorySelect = ({ product }: { product: IItemData }) => {

    const { responseData } = ResponseDataState
    const { } = ItemDataState

    const [productWithSameTitle, setProductWithSameTitle] = useState<IItemData[]>([])
    const [memoryList, setMemoryList] = useState<string[]>([])

    useEffect(() => {
        if (responseData) {
            let memory: string[] = []

            responseData.items.forEach((item: IItemData) => {
                if (product.title === item.title && !memory.includes(item.features.memory)) {
                    memory.push(item.features.memory)
                }

            })
            setMemoryList(memory)
            // setProductWithSameTitle(responseData.items.filter((item) => {
            //     return item.title === product.title
            // }))
            console.log(memory)
        }
    }, [])

    return (
        <div className="memory-select">
            <h3 className="memory-select__colors-title">Объем памяти</h3>
            <ul className="memory-select__colors-list">
                {memoryList.map((color: string) => {

                    //                     const btnColor = {
                    //                         backgroundColor: `${color}`,
                    //                     }
                    // 
                    //                     const borderColor = {
                    //                         border: "1px solid transparent",
                    //                         borderColor: `${color}`,
                    // 
                    //                     }

                    return (
                        <li className="memory-select__color-wrap"
                        // style={product.color === color ? borderColor : undefined}
                        >

                            <button
                                className={
                                    product.color === color
                                        ? "memory-select__color-btn memory-select__color-btn--active"
                                        : "memory-select__color-btn"
                                }
                                // style={btnColor}
                                type="button"
                            // onClick={() => changeColor(product)}
                            >
                            </button>
                        </li>
                    );
                })}

            </ul>
        </div>
    )
}

export default MemorySelect
