import { toJS } from 'mobx';
import { useEffect, useState } from 'react';
import { ICartItem } from '../../../interfaces/ICartItem';
import { IItemData } from '../../../interfaces/IItemData'
import CartState from '../../../store/CartState';
import CloseBtn from '../../svg/CloseBtn';
import Counter from '../Counter/Counter';
import './CartItem.scss'

const CartItem = ({ cartItem }: { cartItem: ICartItem }) => {

    const { cartItems, priceSum } = CartState;
    const { count, item: { title, categoryType, features: { memory }, images, priceInfo: { fullPrice, discountAmount }, amount } } = cartItem;

    const [countToOrder, setCountToOrder] = useState(1)
    const [itemTotalPrice, setItemTotalPrice] = useState(fullPrice - discountAmount)


    useEffect(() => {



        console.log(cartItem.count)

    }, [cartItems])





    const removeFromCart = () => {

        let deleteCount: number | undefined;

        CartState.setCartItems([
            ...cartItems.filter((i) => {
                if (i.item.id === cartItem.item.id) deleteCount = i.count
                return i.item.id !== cartItem.item.id;
            }),
        ]);
        deleteCount && CartState.setPriceSum(priceSum - deleteCount * (fullPrice - discountAmount))

    };

    return (
        <div className="cart-item">



            <img className="cart-item__img" src={images.snippetImage} alt="img" />

            <div className="cart-item__text-wrap">
                <h3 className="cart-item__title">
                    {`${categoryType} ${title}, ${memory}`}
                </h3>
                {cartItem.count > 1 ? (
                    <b className="cart-item__price">
                        <span className="cart-item__price-count">{(fullPrice - discountAmount)} x {cartItem.count} </span>
                        <span className="cart-item__total">{(fullPrice - discountAmount) * cartItem.count} руб.</span>

                    </b>
                ) :
                    <b className="cart-item__price">
                        {(fullPrice - discountAmount)} руб.
                    </b>
                }
            </div>


            <button className="cart-item__delete-btn"
                onClick={() => removeFromCart()}
            >
                <CloseBtn />
            </button>
            <div className="cart-item__counter">
                <Counter countToOrder={countToOrder} setCountToOrder={setCountToOrder} maxCount={amount} itemPrice={fullPrice - discountAmount} cartItem={cartItem} />
            </div>
        </div>
    )
}

export default CartItem
