import { RecordWithTtl } from 'dns';
import React, { useState } from 'react';
import { IItemData } from '../../../interfaces/IItemData';
import CartState from '../../../store/CartState'
import './Counter.scss'

interface ICounter {
    countToOrder: number
    setCountToOrder: React.Dispatch<React.SetStateAction<number>>;
    maxCount: number;
}

const Counter: React.FC<ICounter> = ({ countToOrder, setCountToOrder, maxCount }) => {

    const { cartItems } = CartState

    // const [itemCount, setItemCount] = useState(1)

    const incItem = () => {
        setCountToOrder(countToOrder + 1)
        // setItemCount(itemCount + 1)
        // CartState.setCartItems([...cartItems.concat(item)])
    }


    const decItem = () => {
        setCountToOrder(countToOrder - 1)
        // setItemCount(itemCount - 1)
        // CartState.setCartItems([
        //     ...cartItems.filter((i) => {
        //         return i.id !== item.id;
        //     }),
        // ]);
    }

    return (
        <div className="counter">
            <button className="counter__btn counter__btn--dec"
                onClick={decItem}
            >-</button>
            <div className="counter__num">{countToOrder}</div>
            <button className="counter__btn counter__btn--inc"
                onClick={incItem}
                disabled={maxCount === countToOrder ? true : false}
            >+</button>
        </div>
    )
}

export default Counter
