import { observer } from "mobx-react-lite";
import React, { useEffect, useState } from "react";
import "./Pagination.scss";

interface IPagination {
    itemsCount: number | undefined;
    itemsPerPage: number | undefined;
    startIndex: number | undefined;
    setStartIndex: any;
}

const Pagination: React.FC<IPagination> = observer(({ itemsCount, itemsPerPage, startIndex, setStartIndex }) => {
    const [numbersButtons, setNumbersButtons] = useState<number[] | undefined>();
    const [selectedBtn, setSelectedBtn] = useState<number>(1);

    console.log(itemsCount)
    useEffect(() => {
        let arrOfNumbers: number[] = [];

        if (itemsCount && itemsPerPage) {
            for (let i = 1; i <= Math.ceil(itemsCount / itemsPerPage); i++) {
                arrOfNumbers.push(i);
            }
        }

        setNumbersButtons(arrOfNumbers);
        setStartIndex(0)
    }, [itemsCount]);

    const switchPage = (number: number) => {
        itemsPerPage && setStartIndex((number - 1) * itemsPerPage);
        setSelectedBtn(number)
    };



    return (
        <div className="pagination">
            <ul className="pagination__list">
                {numbersButtons?.map((number) => {
                    return (
                        <li className="pagination__item">
                            <button
                                className={
                                    selectedBtn === number
                                        ? "pagination__btn-number active"
                                        : "pagination__btn-number"
                                }
                                onClick={() => switchPage(number)}
                            >
                                {number}
                            </button>
                        </li>
                    );
                })}
            </ul>
        </div>
    );
})

export default Pagination;
