import { observer } from 'mobx-react-lite';
import React, { useEffect, useState } from 'react'
import ApiService from '../../../API/ApiService';
import { IItemData } from '../../../interfaces/IItemData';
import FiltersSettingsState from '../../../store/FiltersSettingsState';
import ResponseDataState from '../../../store/ResponseDataState';
import ItemCard from '../../ItemCard/ItemCard';
import './ResultsList.scss'

const ResultsList = observer(() => {


    const { responseData } = ResponseDataState
    const { allFilteredItems, resultsWithPagination } = FiltersSettingsState;


    const [results, setResults] = useState<IItemData[]>()

    useEffect(() => {
        if (resultsWithPagination !== undefined) {
            return setResults(resultsWithPagination)
        }

        return setResults(allFilteredItems)
    }, [])



    return (
        <ul className="results-list">
            {/* {resultsPerPage && resultsPerPage.map((item: IItemData) => { */}
            {allFilteredItems && allFilteredItems.map((item: IItemData) => {
                return <ItemCard inPageView="inPage" item={item} />;
            })}
        </ul>
    )
})

export default ResultsList
