import { observer } from "mobx-react-lite";
import React, { useEffect, useState } from "react";
import { IItemData } from "../../interfaces/IItemData";
import FiltersSettingsState from "../../store/FiltersSettingsState";
import "./Pagination.scss";

interface IPagination {
    allFilteredItems: IItemData[] | undefined;
}

const Pagination: React.FC<IPagination> = observer(({ allFilteredItems }) => {

    console.log('pagination')


    const [itemsPerPage, setItemsPerPage] = useState<number>(6);
    const [startIndex, setStartIndex] = useState<number>(0);
    const [numbersButtons, setNumbersButtons] = useState<number[] | undefined>();
    const [selectedBtn, setSelectedBtn] = useState<number>(1);

    useEffect(() => {
        allFilteredItems && FiltersSettingsState.setResultsPerPage(allFilteredItems.slice(startIndex, startIndex + itemsPerPage))

    }, [numbersButtons])

    useEffect(() => {
        let arrOfNumbers: number[] = [];

        if (allFilteredItems && itemsPerPage) {
            for (let i = 1; i <= Math.ceil(allFilteredItems.length / itemsPerPage); i++) {
                arrOfNumbers.push(i);
            }
        }

        setNumbersButtons(arrOfNumbers);
        setStartIndex(0)
        setSelectedBtn(1)


    }, [allFilteredItems]);

    const switchPage = (number: number) => {
        itemsPerPage && setStartIndex((number - 1) * itemsPerPage);
        allFilteredItems && FiltersSettingsState.setResultsPerPage(allFilteredItems.slice(startIndex, startIndex + itemsPerPage))

        setSelectedBtn(number)
    };



    return (
        <div className="pagination">
            <ul className="pagination__list">
                {numbersButtons?.map((number) => {
                    return (
                        <li className="pagination__item">
                            <button
                                className={
                                    selectedBtn === number
                                        ? "pagination__btn-number active"
                                        : "pagination__btn-number"
                                }
                                onClick={() => switchPage(number)}
                            >
                                {number}
                            </button>
                        </li>
                    );
                })}
            </ul>
        </div>
    );
})

export default Pagination;
