import { observer } from "mobx-react-lite";
import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import PageWidthState from "../../../store/PageWidthState";
import "./LinksList.scss";

interface ILinksList {
    items: string[] | undefined;
    linkFunction?: any;
    isListShown: boolean;
}

const LinksList: React.FC<ILinksList> = observer(({ items, linkFunction, isListShown }) => {

    const linksListRef = useRef<HTMLUListElement>(null)

    useEffect(() => {
        if (linksListRef.current !== null) {
            console.log(isListShown && linksListRef.current.clientHeight)
            console.log(window.scrollY)
            if (isListShown) {
                linksListRef.current.classList.add('links-list--on-open')
            }

            if (!isListShown) {
                console.log(window.scrollY)
                linksListRef.current.classList.remove('links-list--on-open')
            }
        }
        // console.log(isListShown);
    }, [isListShown]);

    return (
        <ul className={"links-list"} ref={linksListRef}>
            {isListShown &&
                items &&
                items.map((item) => {
                    return (
                        <Link className="links-list__item" to="/category" onClick={() => linkFunction(item)}>
                            {/* <span>{item}</span> */}
                        </Link>
                    );
                })}
        </ul>
    );
});

export default LinksList;
