import { observer } from 'mobx-react-lite'
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import { IItemData } from '../../../../interfaces/IItemData'
import ItemDataState from '../../../../store/ItemDataState'
import ProductSelectsState from '../../../../store/ProductSelectsState'
import ResponseDataState from '../../../../store/ResponseDataState'
import { setColorName } from '../../../../Utils/setColorName'
import './ColorSelect.scss'

const ColorSelect = observer(({ product }: { product: IItemData }) => {
    const { responseData } = ResponseDataState
    const { itemData, urlName } = ItemDataState

    const [colorsList, setColorsList] = useState<string[]>([])
    const [currentId, setCurrentId] = useState<number | undefined>()

    const { id, color, title } = product

    const params = useParams();

    useEffect(() => {

        if (responseData) {
            let colors: string[] = []

            responseData.items.forEach((item: IItemData) => {
                if (title === item.title && !colors.includes(item.color)) {
                    colors.push(item.color)
                }

            })
            setColorsList(colors)
        }
    }, [])

    const changeUrlId = (color: string) => {
        // params && params.id = '11';
        if (responseData && itemData) {
            let itemToShow = responseData.items.find(item => item.title === itemData.title && item.color === color)
            itemToShow && ItemDataState.setItemData(itemToShow)
        }
        // itemData && ItemDataState.setUrlName(String(itemData.id))


    }



    return (
        <div className="color-select">
            <h3 className="color-select__colors-title">Цвет</h3>
            <ul className="color-select__colors-list">
                {colorsList.map((colorItem: string) => {

                    const colorName = setColorName(colorItem)

                    const btnColor = {
                        backgroundColor: `${colorName}`,
                    }

                    return (
                        <li className={color === colorItem ? "color-select__color-wrap color-select__color-wrap--active" : "color-select__color-wrap"} >



                            <Link to={`/item/${id}`}
                                onClick={() => changeUrlId(colorItem)}
                            >
                                <button
                                    className={
                                        color === colorItem
                                            ? "color-select__color-btn color-select__color-btn--active"
                                            : "color-select__color-btn"
                                    }
                                    style={btnColor}
                                    type="button"
                                // onClick={() => colorsList.length > 1 && ProductSelectsState.setSelectedColor(color)}

                                >
                                </button>
                            </Link>


                        </li>
                    );
                })}

            </ul>
        </div>
    )
})

export default ColorSelect
