import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { CSSTransition } from 'react-transition-group';
import ArrowIcon from '../../svg/ArrowIcon';
import './FooterNavList.scss'

interface IFooterNavList {
    title?: string | undefined;
    items?: string[] | undefined;
}

const FooterNavList: React.FC<IFooterNavList> = ({ title, items }) => {

    const [isListShown, setIsListShown] = useState(false)

    return (
        <div className='footer-nav-list'>
            <button className='footer-nav-list__title-button'>
                <h3 className='footer-nav-list__title'>{title}</h3>
                <span className='footer-nav-list__arrow'><ArrowIcon /></span>
            </button>

            <CSSTransition in={isListShown}
                timeout={1500}
                mountOnEnter
                unmountOnExit>
                <ul className='footer-nav-list__links-list'>
                    {items && items.map((item) => {
                        return (
                            <li className='footer-nav-list__item'>
                                <Link to="/category">
                                    {item}
                                </Link>
                            </li>

                        )
                    })}
                </ul>
            </CSSTransition>

        </div>
    )
}

export default FooterNavList
