import { useEffect, useState } from 'react'
import './Pagination.scss'

const Pagination = ({ itemsCount, itemsPerPage }: { itemsCount: number, itemsPerPage: number }) => {


    const [numbersButtons, setNumbersButtons] = useState<number[] | undefined>();



    useEffect(() => {
        let arrOfNumbers: number[] = []
        for (let i = 1; i <= itemsCount / itemsPerPage; i++) {
            arrOfNumbers.push(i)
        }
        setNumbersButtons(arrOfNumbers)
        console.log(arrOfNumbers)
    }, [])





    return (
        <div className='pagination'>
            <ul>
                {numbersButtons?.map((number) => {
                    return (
                        <li>{number}</li>
                    )
                })}
            </ul>
        </div>
    )
}

export default Pagination
