import React, { useEffect } from 'react'
import FiltersSettingsState from '../../store/FiltersSettingsState'

const ItemsFoundCounter = () => {

    const { itemsToShow, itemsFound } = FiltersSettingsState

    useEffect(() => {

        FiltersSettingsState.setItemsFound(itemsToShow?.length)
    }, [itemsToShow])

    return (
        <b >
            Товаров найдено ({itemsFound})
        </b>
    )
}

export default ItemsFoundCounter
