import { toJS } from 'mobx';
import { useEffect, useState } from 'react';
import { IItemData } from '../../../interfaces/IItemData'
import CartState from '../../../store/CartState';
import CloseBtn from '../../svg/CloseBtn';
import Counter from '../Counter/Counter';
import './CartItem.scss'

const CartItem = ({ item }: { item: IItemData }) => {

    const { cartItems, priceSum } = CartState;
    const { title, categoryType, features: { memory }, images, priceInfo: { fullPrice, discountAmount }, amount } = item;

    const [countToOrder, setCountToOrder] = useState(1)


    useEffect(() => {
        return () => {
            console.log('unmounted')

            CartState.setPriceSum(priceSum - countToOrder * (fullPrice - discountAmount))
        }
    }, [])

    useEffect(() => {
        console.log('countToOrder')
        countToOrder === 0 && removeFromCart(item)
    }, [countToOrder])



    const removeFromCart = (item: IItemData) => {
        const { priceInfo: { fullPrice, discountAmount }, amount } = item
        CartState.setCartItems([
            ...cartItems.filter((i) => {
                return i.id !== item.id;
            }),
        ]);

        // CartState.setPriceSum(priceSum - (fullPrice - discountAmount))
    };

    return (
        <div className="cart-item">



            <img className="cart-item__img" src={images.snippetImage} alt="img" />

            <div className="cart-item__text-wrap">
                <h3 className="cart-item__title">
                    {`${categoryType} ${title}, ${memory}`}
                </h3>
                {countToOrder > 1 ? (
                    <b className="cart-item__price">
                        <span className="cart-item__price-count">{(fullPrice - discountAmount)} x {countToOrder} </span>
                        <span className="cart-item__total">{(fullPrice - discountAmount) * countToOrder} руб.</span>

                    </b>
                ) :
                    <b className="cart-item__price">
                        {(fullPrice - discountAmount)} руб.
                    </b>
                }
            </div>


            <button className="cart-item__delete-btn"
                onClick={() => removeFromCart(item)}
            >
                <CloseBtn />
            </button>
            <div className="cart-item__counter">
                <Counter countToOrder={countToOrder} setCountToOrder={setCountToOrder} maxCount={item.amount} itemPrice={fullPrice - discountAmount} />
            </div>
        </div>
    )
}

export default CartItem
