import { observer } from 'mobx-react-lite';
import { useEffect, useState } from 'react';
import { IItemData } from '../../interfaces/IItemData';
import FiltersSettingsState from '../../store/FiltersSettingsState';
import { SetFilterSettings } from '../../Utils/SetFilterSettings';
import ClearFiltersBtn from '../buttons/ResetFiltersBtn/ResetFiltersBtn';
import ItemCard from '../ItemCard/ItemCard';
import Pagination from '../Pagination/Pagination';
import SortingSelect from '../SortingSelect/SortingSelect';
import CatalogIcon from '../svg/CatalogIcon';
import './ResultsList.scss'

interface IResultsList {
    responseItems: IItemData[] | undefined;
}

const ResultsList: React.FC<IResultsList> = observer(({ responseItems }) => {

    console.log('results')

    const { itemsToShow } = FiltersSettingsState;
    const [itemsPerPage, setItemsPerPage] = useState<number>(6);
    const [startIndex, setStartIndex] = useState<number>(0);




    useEffect(() => {
        responseItems && FiltersSettingsState.setItemsToShow(responseItems)
    }, [responseItems])

    // useEffect(() => {
    //     FiltersSettingsState.setItemsToShow(SetFilterSettings());
    // }, [selectedCategories, selectedBrands, priceRange, isAvailable, isDiscount]);


    const showFilters = () => {
        // setIsFiltersPanelShown(!isFiltersPanelShown);
    };

    return (
        <div className='results-list'>


            <div className="catalog-page__items-wrap">
                {itemsToShow && (
                    <>
                        {/* <Pagination
                            itemsCount={itemsToShow.length}
                            itemsPerPage={itemsPerPage}
                            startIndex={startIndex}
                            setStartIndex={setStartIndex}
                        /> */}
                        <div className="catalog-page__mobile-settings">
                            <button className="catalog-page__btn-show-filters" onClick={showFilters}>
                                <CatalogIcon />
                                <span>Фильтры</span>
                            </button>
                            <div className="catalog-page__sorting-by-wrap">
                                <SortingSelect />
                            </div>
                        </div>
                        <ul className="catalog-page__results-list results-list">
                            {itemsToShow && itemsToShow
                                .slice(startIndex, startIndex + itemsPerPage)
                                .map((item: IItemData) => {
                                    return <ItemCard inPageView="inPage" item={item} />;
                                })}
                        </ul>
                    </>
                )}
                {itemsToShow?.length === 0 && (
                    <div className="catalog-page__results-not-found">
                        <p>По данным параметрам ничего не найдено</p>
                        <ClearFiltersBtn />
                    </div>
                )}
            </div>

        </div>
    )
})

export default ResultsList
