import { RecordWithTtl } from 'dns';
import { toJS } from 'mobx';
import React, { useState } from 'react';
import { ICartItem } from '../../../interfaces/ICartItem';
import { IItemData } from '../../../interfaces/IItemData';
import CartState from '../../../store/CartState'
import './Counter.scss'

interface ICounter {
    countToOrder: number
    setCountToOrder: React.Dispatch<React.SetStateAction<number>>;
    maxCount: number;
    itemPrice?: number;
    cartItem?: ICartItem | undefined;
}

const Counter: React.FC<ICounter> = ({ countToOrder, setCountToOrder, maxCount, itemPrice, cartItem }) => {

    const { cartItems, priceSum } = CartState;



    const incItem = () => {


        CartState.setCartItems([...cartItems.map((item) => {
            if (item.item.id === cartItem?.item.id) {
                ++item.count
            }
            return item
        })])
    }


    const decItem = () => {

        CartState.setCartItems([...cartItems.map((item) => {
            if (item.count === 0) removeFromCart()
            if (item.item.id === cartItem?.item.id) {
                --item.count
            }
            return item
        })])
    }

    const removeFromCart = () => {

        let deleteCount: number | undefined;

        cartItem && CartState.setCartItems([
            ...cartItems.filter((i) => {
                if (i.item.id === cartItem.item.id) deleteCount = i.count
                return i.item.id !== cartItem.item.id;
            }),
        ]);

    };

    return (
        <div className="counter">
            <button className="counter__btn counter__btn--dec"
                onClick={decItem}
            >-</button>
            <div className="counter__num">{cartItem?.count}</div>
            <button className="counter__btn counter__btn--inc"
                onClick={incItem}
                disabled={maxCount === countToOrder ? true : false}
            >+</button>
        </div>
    )
}

export default Counter
