import { useEffect, useState } from 'react'
import './Pagination.scss'


interface IPagination {
    itemsCount: number, itemsPerPage: number
}

const Pagination = ({ itemsCount, itemsPerPage }: {}) => {


    const [numbersButtons, setNumbersButtons] = useState<number[] | undefined>();

    const [buttonsCount, setButtonsCount] = useState(Math.ceil(itemsCount / itemsPerPage))

    useEffect(() => {
        let arrOfNumbers: number[] = []
        for (let i = 1; i <= buttonsCount; i++) {
            arrOfNumbers.push(i)
        }
        setNumbersButtons(arrOfNumbers)
        console.log(arrOfNumbers)
    }, [])





    return (
        <div className='pagination'>
            <ul>
                {numbersButtons?.map((number) => {
                    return (
                        <li><button onClick={() => console.log(number)}>{number}</button></li>
                    )
                })}
            </ul>
        </div>
    )
}

export default Pagination
