										<TabsList tabName={tabName} setTabName={setTabName} />
										<div className="data-tabs__results">
											{tabName === "features" && <Features features={itemData.features} />}
										</div>
										<div className="data-tabs__results">
											{tabName === "reviews" && (
												<ReviewsList reviewData={itemData.reviewsInfo.reviews} />
											)}
										</div>
									</div>
								</div>
							</div>
						</div>
					)}
				</div>
